// SINGLE PRODUCT DETAILS PAGE TEMPLATE
// Dynamically generated by gatsby
import React from "react"
import { graphql, Link } from "gatsby"
import Image from "gatsby-image"
import Layout from "../components/layout"

// Destructuring all the properties from the query
const SingleProduct = ({
  data: {
    product: {
      title,
      price,
      image: {
        childImageSharp: { fixed },
      },
      description,
    },
  },
}) => {
  return (
    <Layout>
      <h1 style={{ textAlign: "center", padding: "80px 0" }}>
        Single Product: {title}
      </h1>
      <section className="SingleProduct">
        <article>
          <Image fixed={fixed} alt={title} />
        </article>
        <article>
          <h1>{title}</h1>
          <h3>${price}</h3>
          <p>{description}</p>
          <Link to="/contact" className="addToCartBtn">
            Contact me
          </Link>
          <Link
            style={{ textAlign: "center", marginLeft: "20px" }}
            to="/products"
          >
            back to products
          </Link>
        </article>
      </section>
    </Layout>
  )
}

export const query = graphql`
  query GetProducts($url: String) {
    product: strapiProduct(url: { eq: $url }) {
      title
      price
      description
      id
      image {
        childImageSharp {
          fixed(width: 440) {
            base64
            width
            height
            src
            srcSet
          }
        }
      }
    }
  }
`

export default SingleProduct
